import { EthereumCode } from './../../../Ethereum/models';
import { assert } from 'chai';
import Mocha = require('mocha');

// tslint:disable-next-line
const code1 = "";
// tslint:disable-next-line
const code2 = "";

Mocha.describe('EthereumCode', () => {
    Mocha.describe('CreateHash', () => {
        Mocha.it('should create the same hash for two different metadata stamps', () => {
            const expected = (new EthereumCode(code1)).Hash();
            const result = (new EthereumCode(code2)).Hash();
            assert.equal(result, expected);
        });
    });

});